mapper.select:
select t.rNo, t.rName, t.userName, t.passWord, t.userTypeName from((select ut.rNo, stu.sName as rName, ut.userName, ut.passWord, utt.userTypeName from user_tab ut join user_type_tab utt on ut.userTypeId=utt.userTypeId join stu on ut.rno=stu.sNo
1=1
and sno=#{rNo}
and sName like '%${rName}%'
and userName=#{userName}
)
union
(select ut.rNo, tea.tName as rName, ut.userName, ut.passWord, utt.userTypeName from user_tab ut join user_type_tab utt on ut.userTypeId=utt.userTypeId join teacher tea on ut.rno=tea.tNo
1=1
and tno=#{rNo}
and tName like '%${rName}%'
and userName=#{userName}
))t

mapper.update:
update user_tab 
set

	user_tab.passWord=#{passWord},


	user_tab.userTypeId=#{userTypeId}


	user_tab.userName=#{userName}
